 const canvas = document.getElementById('background-canvas');
 const ctx = canvas.getContext('2d');

 canvas.width = window.innerWidth;
 canvas.height = window.innerHeight;

 const particles = [];
 const mouse = { x: null, y: null, radius: 150 };
 let time = 0;

 class Particle {
 constructor() {
 this.x = Math.random() * canvas.width;
 this.y = Math.random() * canvas.height;
 this.size = Math.random() * 3 + 1;
 this.baseX = this.x;
 this.baseY = this.y;
 this.density = Math.random() * 40 + 10;
 this.speedX = Math.random() * 2 - 1;
 this.speedY = Math.random() * 2 - 1;
 this.angle = Math.random() * Math.PI * 2;
 this.angleSpeed = Math.random() * 0.05 - 0.025;
 this.pulseSpeed = Math.random() * 0.05 + 0.02;
 this.baseSize = this.size;
 this.color = `hsl(${Math.random() * 60 + 200}, 70%, ${Math.random() * 30 + 60}%)`;
 this.glowColor = `rgba(100, 200, 255, ${Math.random() * 0.3 + 0.1})`;
 }

 draw() {
 // 粒子发光效果
 ctx.shadowBlur = 15;
 ctx.shadowColor = this.glowColor;
 ctx.fillStyle = this.color;
 ctx.beginPath();
 ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
 ctx.closePath();
 ctx.fill();
 ctx.shadowBlur = 0;
 }

 update() {
 // 粒子自主运动
 this.x += this.speedX + Math.sin(time * 0.002 + this.angle) * 0.5;
 this.y += this.speedY + Math.cos(time * 0.002 + this.angle) * 0.5;

 // 边界检查
 if (this.x < 0) this.x = canvas.width;
 if (this.x > canvas.width) this.x = 0;
 if (this.y < 0) this.y = canvas.height;
 if (this.y > canvas.height) this.y = 0;

 // 角度旋转
 this.angle += this.angleSpeed;

 // 大小脉动效果
 this.size = this.baseSize + Math.sin(time * this.pulseSpeed) * 1;

 // 鼠标互动
 if (mouse.x && mouse.y) {
 let dx = mouse.x - this.x;
 let dy = mouse.y - this.y;
 let distance = Math.sqrt(dx * dx + dy * dy);

 if (distance < mouse.radius) {
 let forceDirectionX = dx / distance;
 let forceDirectionY = dy / distance;
 let maxDistance = mouse.radius;
 let force = (maxDistance - distance) / maxDistance;

 this.x -= forceDirectionX * force * this.density * 0.1;
 this.y -= forceDirectionY * force * this.density * 0.1;
 }
 }

 this.draw();
 }
 }

 function init() {
 particles.length = 0;
 for (let i = 0; i < 150; i++) {
 particles.push(new Particle());
 }
 }

 function connectParticles() {
 for (let a = 0; a < particles.length; a++) {
 for (let b = a + 1; b < particles.length; b++) {
 let dx = particles[a].x - particles[b].x;
 let dy = particles[a].y - particles[b].y;
 let distance = Math.sqrt(dx * dx + dy * dy);

 if (distance < 120) {
 let opacity = 0.3 * (1 - distance / 120);

 // 线条渐变效果
 let gradient = ctx.createLinearGradient(
 particles[a].x, particles[a].y,
 particles[b].x, particles[b].y
 );
 gradient.addColorStop(0, particles[a].glowColor);
 gradient.addColorStop(1, particles[b].glowColor);

 ctx.strokeStyle = gradient;
 ctx.lineWidth = 1 + Math.sin(time * 0.01) * 0.5;
 ctx.beginPath();
 ctx.moveTo(particles[a].x, particles[a].y);
 ctx.lineTo(particles[b].x, particles[b].y);
 ctx.stroke();
 }
 }
 }
 }

 function createTrailEffect() {
 // 添加拖尾效果
 ctx.fillStyle = 'rgba(10, 10, 10, 0.05)';
 ctx.fillRect(0, 0, canvas.width, canvas.height);
 }

 function animate() {
 time++;

 // 使用半透明填充创建拖尾效果
 ctx.fillStyle = 'rgba(10, 10, 10, 0.08)';
 ctx.fillRect(0, 0, canvas.width, canvas.height);

 // 绘制粒子
 for (let i = 0; i < particles.length; i++) {
 particles[i].update();
 }

 // 连接粒子
 connectParticles();

 requestAnimationFrame(animate);
 }

 window.addEventListener('mousemove', function(event) {
 mouse.x = event.x;
 mouse.y = event.y;
 });

 window.addEventListener('mouseout', function() {
 mouse.x = null;
 mouse.y = null;
 });

