    // --- 1. Canvas Particle Network Animation ---
    const canvas = document.getElementById('background-canvas');
    const ctx = canvas.getContext('2d');

    let width, height;
    let particles = [];
    const particleCount = 70;
    const connectionDistance = 150;
    const mouseDistance = 200;

    let mouse = { x: null, y: null };

    // Resize handler
    function resize() {
        width = canvas.width = window.innerWidth;
        height = canvas.height = window.innerHeight;
    }
    window.addEventListener('resize', resize);
    resize();

    // Mouse tracker
    window.addEventListener('mousemove', (e) => {
        mouse.x = e.x;
        mouse.y = e.y;

        // Call 3D Tilt function
        handleTilt(e);
    });

    window.addEventListener('mouseout', () => {
        mouse.x = null;
        mouse.y = null;

        // Reset Tilt
        resetTilt();
    });

    // Particle Class
    class Particle {
        constructor() {
            this.x = Math.random() * width;
            this.y = Math.random() * height;
            this.vx = (Math.random() - 0.5) * 1.5;
            this.vy = (Math.random() - 0.5) * 1.5;
            this.size = Math.random() * 2 + 1;
            this.color = '#00f3ff'; // Cyan
        }

        update() {
            this.x += this.vx;
            this.y += this.vy;

            // Bounce off edges
            if (this.x < 0 || this.x > width) this.vx *= -1;
            if (this.y < 0 || this.y > height) this.vy *= -1;

            // Mouse interaction
            if (mouse.x != null) {
                let dx = mouse.x - this.x;
                let dy = mouse.y - this.y;
                let distance = Math.sqrt(dx*dx + dy*dy);

                if (distance < mouseDistance) {
                    const forceDirectionX = dx / distance;
                    const forceDirectionY = dy / distance;
                    const force = (mouseDistance - distance) / mouseDistance;
                    const directionX = forceDirectionX * force * 2;
                    const directionY = forceDirectionY * force * 2;

                    this.vx -= directionX * 0.05;
                    this.vy -= directionY * 0.05;
                }
            }
        }

        draw() {
            ctx.beginPath();
            ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
            ctx.fillStyle = this.color;
            ctx.fill();
        }
    }

    // Initialize particles
    for (let i = 0; i < particleCount; i++) {
        particles.push(new Particle());
    }

    // Animation Loop
    function animate() {
        ctx.clearRect(0, 0, width, height);

        // Update and draw particles
        particles.forEach(p => {
            p.update();
            p.draw();
        });

        // Draw connections
        connectParticles();

        requestAnimationFrame(animate);
    }

    function connectParticles() {
        let opacityValue = 1;
        for (let a = 0; a < particles.length; a++) {
            for (let b = a; b < particles.length; b++) {
                let dx = particles[a].x - particles[b].x;
                let dy = particles[a].y - particles[b].y;
                let distance = Math.sqrt(dx * dx + dy * dy);

                if (distance < connectionDistance) {
                    opacityValue = 1 - (distance / connectionDistance);
                    ctx.strokeStyle = 'rgba(0, 243, 255,' + opacityValue * 0.2 + ')';
                    ctx.lineWidth = 1;
                    ctx.beginPath();
                    ctx.moveTo(particles[a].x, particles[a].y);
                    ctx.lineTo(particles[b].x, particles[b].y);
                    ctx.stroke();
                }
            }

            // Connect to mouse
            if (mouse.x != null) {
                let dx = particles[a].x - mouse.x;
                let dy = particles[a].y - mouse.y;
                let distance = Math.sqrt(dx*dx + dy*dy);
                if (distance < connectionDistance) {
                    ctx.strokeStyle = 'rgba(188, 19, 254,' + 0.3 + ')';
                    ctx.lineWidth = 1;
                    ctx.beginPath();
                    ctx.moveTo(particles[a].x, particles[a].y);
                    ctx.lineTo(mouse.x, mouse.y);
                    ctx.stroke();
                }
            }
        }
    }

    animate();

    // --- 2. 3D Tilt Effect for Card ---
    const card = document.getElementById('card');

    function handleTilt(e) {
        if(!card) return;

        const xAxis = (window.innerWidth / 2 - e.pageX) / 25;
        const yAxis = (window.innerHeight / 2 - e.pageY) / 25;

        // Limit the rotation
        const clampX = Math.max(-15, Math.min(15, xAxis));
        const clampY = Math.max(-15, Math.min(15, yAxis));

        card.style.transform = `rotateY(${clampX}deg) rotateX(${clampY}deg)`;
    }

    function resetTilt() {
        if(!card) return;
        card.style.transform = `rotateY(0deg) rotateX(0deg)`;
    }

    // --- 3. Typewriter Effect ---
    const textElement = document.getElementById('typewriter');
    const phrases = ['Cloud Solution Architect', 'Technical Account Manager', 'Tencent'];
    let phraseIndex = 0, charIndex = 0, isDeleting = false, typeSpeed = 100;

    function type() {
        const current = phrases[phraseIndex];
        if (isDeleting) {
            textElement.textContent = current.substring(0, charIndex - 1);
            charIndex--;
            typeSpeed = 50;
        } else {
            textElement.textContent = current.substring(0, charIndex + 1);
            charIndex++;
            typeSpeed = 150;
        }
        if (!isDeleting && charIndex === current.length) {
            isDeleting = true;
            typeSpeed = 2000;
        } else if (isDeleting && charIndex === 0) {
            isDeleting = false;
            phraseIndex = (phraseIndex + 1) % phrases.length;
            typeSpeed = 500;
        }
        setTimeout(type, typeSpeed);
    }
    document.addEventListener('DOMContentLoaded', type);

    // --- 4. Contact Modal ---
    document.getElementById('contact-btn').addEventListener('click', function(e) {
        e.preventDefault();
        const modal = document.createElement('div');
        modal.style.cssText = `
            position:fixed;top:0;left:0;width:100%;height:100%;
            background:rgba(0,0,0,0.8);display:flex;
            justify-content:center;align-items:center;z-index:1000;
        `;
        const box = document.createElement('div');
        box.style.cssText = `
            background:rgba(10, 10, 16, 0.9);padding:40px;border-radius:15px;
            border:2px solid var(--primary);max-width:500px;width:90%;
            box-shadow:0 0 50px rgba(0, 243, 255, 0.3);position:relative;
            backdrop-filter: blur(10px);
        `;
        box.innerHTML = `
            <h2 style="margin-bottom:20px;text-align:center;color:var(--primary);font-family:'Orbitron',sans-serif;">CONTACT TRANSMISSION</h2>
            <form id="contact-form" style="display:flex;flex-direction:column;gap:15px;">
                <input type="text" placeholder="IDENTITY" required style="padding:12px;background:#111;color:white;border:1px solid #333;border-radius:5px;font-family:'Share Tech Mono',monospace;">
                <input type="email" placeholder="COMMUNICATION CHANNEL" required style="padding:12px;background:#111;color:white;border:1px solid #333;border-radius:5px;font-family:'Share Tech Mono',monospace;">
                <textarea placeholder="MESSAGE CONTENT" required rows="4" style="padding:12px;background:#111;color:white;border:1px solid #333;border-radius:5px;resize:vertical;font-family:'Share Tech Mono',monospace;"></textarea>
