        // 验证必填字段
        if (!name || !email || !subject || !message) {
            return res.status(400).json({
                success: false,
                message: '所有字段都是必填的'
            });
        }

        // 验证邮箱格式
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(email)) {
            return res.status(400).json({
                success: false,
                message: '邮箱格式不正确'
            });
        }

        // 邮件内容
        const mailOptions = {
            from: process.env.SMTP_USER || 'sheaaazuzu@gmail.com',
            to: 'sheazuzu@hotmail.com', // 发送到您的邮箱
            subject: `博客联系表单: ${subject}`,
            html: `
                <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
                    <h2 style="color: #333;">新的联系表单提交</h2>
                    <div style="background: #f5f5f5; padding: 20px; border-radius: 5px;">
                        <p><strong>姓名:</strong> ${name}</p>
                        <p><strong>邮箱:</strong> ${email}</p>
                        <p><strong>主题:</strong> ${subject}</p>
                        <p><strong>消息:</strong></p>
                        <div style="background: white; padding: 15px; border-left: 4px solid #0078d4; margin-top: 10px;">
                            ${message.replace(/\n/g, '<br>')}
                        </div>
                    </div>
                    <p style="color: #666; font-size: 12px; margin-top: 20px;">
                        此邮件来自您的个人博客网站联系表单
                    </p>
                </div>
            `,
            text: `
新的联系表单提交

姓名: ${name}
邮箱: ${email}
主题: ${subject}
消息:
${message}

此邮件来自您的个人博客网站联系表单
            `
        };

        // 发送邮件
        const info = await transporter.sendMail(mailOptions);

        console.log('邮件发送成功:', info.messageId);

        res.json({
            success: true,
            message: '消息发送成功！我会尽快回复您。',
            messageId: info.messageId
        });

    } catch (error) {
        console.error('邮件发送失败:', error);

        res.status(500).json({
            success: false,
            message: '消息发送失败，请稍后重试或直接发送邮件至 sheazuzu@hotmail.com'
        });
    }
});

// 测试SMTP连接
app.get('/test-smtp', async (req, res) => {
    try {
        console.log(' SMTP连接测试开始...');
        console.log(' 当前SMTP配置:');
        console.log('- SMTP_HOST:', process.env.SMTP_HOST || 'smtp.gmail.com');
        console.log('- SMTP_PORT:', process.env.SMTP_PORT || 587);
        console.log('- SMTP_USER:', process.env.SMTP_USER || 'sheaaazuzu@gmail.com');
        console.log('- SMTP_PASS:', process.env.SMTP_PASS ? '已设置（隐藏）' : '未设置');

        console.log(' 开始SMTP连接验证...');
        await transporter.verify();

        console.log('✅ SMTP连接验证成功');
        res.json({
            success: true,
            message: 'SMTP连接测试成功',
            config: {
                host: process.env.SMTP_HOST || 'smtp.gmail.com',
                port: process.env.SMTP_PORT || 587,
                user: process.env.SMTP_USER || 'sheaaazuzu@gmail.com',
                authMethod: 'Modern Authentication'
            }
        });
    } catch (error) {
        console.error('❌ SMTP连接测试失败:', error.message);
        console.error(' 错误详情:', {
            code: error.code,
            response: error.response,
            command: error.command
        });

        res.status(500).json({
            success: false,
            message: 'SMTP连接测试失败: ' + error.message,
            errorDetails: {
                code: error.code,
                response: error.response,
                command: error.command
            },
            troubleshooting: {
                issue: 'Hotmail/Outlook已禁用基本身份验证',
                solution: '请使用应用专用密码而不是主密码',
                guide: '参考 SMTP_SETUP.md 获取详细配置指南'
            }
        });
    }
});

